-- 1. Properties (3 site)
INSERT INTO properties (id, name, address, manager) VALUES
  ('PROP_RESORT', 'Furama Resort', 'Furama Resort Danang', 'IT'),
  ('PROP_VILLAS', 'Furama Villas', 'Furama Villas Danang', 'IT'),
